// SPDX-License-Identifier: GPL

pragma solidity 0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";
import "../interfaces/IGameNFT.sol";
import "../interfaces/IMarketPlace.sol";
import "../interfaces/IFOTAPricer.sol";
import "../libs/chainlink/VRFConsumerBaseUpgradable.sol";
import "../libs/zeppelin/token/BEP20/IBEP20.sol";
import "../libs/fota/Auth.sol";
import "../libs/fota/Math.sol";
import "../interfaces/IFarm.sol";

abstract contract BaseUpgrading is Auth, EIP712Upgradeable, VRFConsumerBaseUpgradable {
  enum Implementation {
    Item,
    Hero
  }
  mapping (address => uint) public nonces;
  IBEP20 public busdToken;
  IBEP20 public usdtToken;
  IBEP20 public fotaToken;
  IFOTAPricer public fotaPricer;
  IMarketPlace.PaymentType public paymentType;
  IFarm public farmContract;
  address public fundAdmin;
  uint constant decimal3 = 1000;
  uint256 constant internal oracleFee = 0.1 * 10 ** 18;
  bytes32 private constant keyHash = 0xcaf3c3727e033261d383b315559476f48034c13b18f8cafed4d871abe5049186;
  uint256 private oracleRandomSeed;

  event PaymentTypeChanged(IMarketPlace.PaymentType newMethod);

  function initialize(
    string memory _name,
    string memory _version,
    address _mainAdmin,
    address _fundAdmin,
    address _fotaPricer
  ) internal initializer {
    Auth.initialize(_mainAdmin);
    EIP712Upgradeable.__EIP712_init(_name, _version);
    fundAdmin = _fundAdmin;
    fotaPricer = IFOTAPricer(_fotaPricer);
    fotaToken = IBEP20(0x69c3FF5F466757BF99feF916aE9a191d7c13696c);
    busdToken = IBEP20(0xD8aD05ff852ae4EB264089c377501494EA1D03C9);
    usdtToken = IBEP20(0xa1D3c78f0f98f6ba7b08ec15df48E22880a0024c);
    VRFConsumerBaseUpgradable.initialize(0xa555fC018435bef5A13C6c6870a9d4C11DEC329C, 0x84b9B910527Ad5C03A9Ca831909E21e236EA7b06);
  }

  function _validateSeed(string calldata _seed, bytes memory _signature) virtual internal;

  function updateFundAdmin(address _address) onlyMainAdmin external {
    require(_address != address(0), "MarketPlace: invalid address");
    fundAdmin = _address;
  }

  function updatePaymentType(IMarketPlace.PaymentType _type) external onlyMainAdmin {
    paymentType = _type;
    emit PaymentTypeChanged(_type);
  }

  function updateFarmContract(address _farmContract) external onlyMainAdmin {
    farmContract = IFarm(_farmContract);
    uint256 MAX_INT = 2**256 - 1;
    fotaToken.approve(_farmContract, MAX_INT);
  }

  function isSuccess(string calldata _seed, uint8 _acceptedRatio) internal returns (bool) {
    uint8 randomNumber = Math.genRandomNumber(_seed, oracleRandomSeed);
    _getRandomNumberFromChainLink();
    return randomNumber < _acceptedRatio;
  }

  function takeFund(IMarketPlace.PaymentCurrency _paymentCurrency, uint _upgradingFee, Implementation _implementation) internal {
    if (paymentType == IMarketPlace.PaymentType.fota) {
      _takeFundFOTA(_upgradingFee * decimal3 / fotaPricer.fotaPrice(), _implementation);
    } else if (paymentType == IMarketPlace.PaymentType.usd) {
      _takeFundUSD(_paymentCurrency, _upgradingFee);
    } else if (_paymentCurrency == IMarketPlace.PaymentCurrency.fota) {
      _takeFundFOTA(_upgradingFee * decimal3 / fotaPricer.fotaPrice(), _implementation);
    } else {
      _takeFundUSD(_paymentCurrency, _upgradingFee);
    }
  }

  function _takeFundFOTA(uint _upgradingFee, Implementation _implementation) private {
    require(fotaToken.allowance(msg.sender, address(this)) >= _upgradingFee, "please approve fota first");
    require(fotaToken.balanceOf(msg.sender) >= _upgradingFee, "please fund your account");
    require(fotaToken.transferFrom(msg.sender, address(this), _upgradingFee), "transfer fota failed");
    if (_implementation == Implementation.Item) {
      require(fotaToken.transfer(fundAdmin, _upgradingFee), "NFT Upgrading: transfer usd token failed");
    } else {
      farmContract.fundFOTA(_upgradingFee);
    }
  }

  function _takeFundUSD(IMarketPlace.PaymentCurrency _paymentCurrency, uint _upgradingFee) private {
    require(_paymentCurrency != IMarketPlace.PaymentCurrency.fota, "payment currency invalid");
    IBEP20 usdToken = _paymentCurrency == IMarketPlace.PaymentCurrency.busd ? busdToken : usdtToken;
    require(usdToken.allowance(msg.sender, address(this)) >= _upgradingFee, "NFT Upgrading: please approve usd token first");
    require(usdToken.balanceOf(msg.sender) >= _upgradingFee, "NFT Upgrading: insufficient balance");
    require(usdToken.transferFrom(msg.sender, address(this), _upgradingFee), "NFT Upgrading: transfer usd token failed");
    require(usdToken.transfer(fundAdmin, _upgradingFee), "NFT Upgrading: transfer usd token failed");
  }

  function _getRandomNumberFromChainLink() private returns (bytes32 requestId) {
    require(LINK.balanceOf(address(this)) >= oracleFee, "NFT Upgrading: Not enough LINK - fill contract with faucet");
    return requestRandomness(keyHash, oracleFee);
  }

  function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
    requestId;
    oracleRandomSeed = randomness;
  }

  function drainToken(address _tokenAddress) external onlyMainAdmin {
    IBEP20 token = IBEP20(_tokenAddress);
    token.transfer(msg.sender, token.balanceOf(address(this)));
  }
}
