// SPDX-License-Identifier: GPL

pragma solidity 0.8.0;

import "../libs/fota/Auth.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "../interfaces/IGameNFT.sol";
import "../interfaces/IMarketPlace.sol";
import "../interfaces/IEnergyManager.sol";
import "../interfaces/IEnergyManager.sol";

abstract contract EnergyAuth is Auth, ContextUpgradeable {
  mapping(address => bool) gameContracts;
  mapping(address => bool) pointContracts;

  function initialize(address _mainAdmin) virtual override public {
    Auth.initialize(_mainAdmin);
  }

  modifier onlyGameContract() {
    require(_isGameContracts() || _isMainAdmin(), "NFTAuth: Only game contract");
    _;
  }

  modifier onlyPointContract() {
    require(_isPointContracts() || _isMainAdmin(), "NFTAuth: Only point contract");
    _;
  }

  function _isGameContracts() internal view returns (bool) {
    return gameContracts[_msgSender()];
  }

  function _isPointContracts() internal view returns (bool) {
    return pointContracts[_msgSender()];
  }

  function updateGameContract(address _contract, bool _status) onlyMainAdmin external {
    require(_contract != address(0), "NFTAuth: Address invalid");
    gameContracts[_contract] = _status;
  }

  function updatePointContract(address _contract, bool _status) onlyMainAdmin external {
    require(_contract != address(0), "NFTAuth: Address invalid");
    pointContracts[_contract] = _status;
  }
}

contract EnergyManagerBak is EnergyAuth {
  struct Energy {
    uint amount;
    uint from;
    uint to;
  }
  IGameNFT public heroNft;
  IMarketPlace public marketPlace;
  mapping (address => Energy) public energies;
  mapping (address => int) public userTempPoint;
  uint public maxEnergy;
  uint public energyMultiplication;
//  uint constant secondInADay = 86400; // 24 * 60 * 60
  uint constant secondInADay = 1800; // 24 * 60 * 60

  event MaxEnergyUpdated(uint amount, uint timestamp);
  event EnergyMultiplicationUpdated(uint amount, uint timestamp);
  event EnergyUpdated(
    address indexed user,
    uint remainingEnergy,
    bool firstConsumeInDay,
    address gameAddress
  );

  function initialize(address _mainAdmin) override public initializer {
    super.initialize(_mainAdmin);
    maxEnergy = 30;
    energyMultiplication = 1;
//    heroNft = IGameNFT(0x9F007C4Ef2fCFaacA5f135680Df4b596195ed61d); TODO mainnet
//    marketPlace = IMarketPlace(0x57573e2239C0A9E67E74D8AC8EA590E8FD99fA63);
  }

  function updateEnergy(address _user, uint _consumeAmount) external onlyGameContract returns (bool) {
    Energy storage energy = energies[msg.sender];
    bool firstConsumeInDay = block.timestamp > energy.to;
    if (firstConsumeInDay) {
      energy.amount = _calculateUserTodayEnergy() - _consumeAmount;
      energy.from = _getStartOfDay();
      energy.to = energy.from + secondInADay;
    } else {
      require(energy.amount >= _consumeAmount, "EnergyManager: consume amount invalid");
      energy.amount -= _consumeAmount;
    }
    emit EnergyUpdated(_user, energy.amount, firstConsumeInDay, msg.sender);
    return firstConsumeInDay;
  }

  function updatePoint(address _user, int _point) external onlyPointContract {
    Energy storage energy = energies[_user];
    bool firstPointInDay = block.timestamp > energy.to;
    if (firstPointInDay) {
      userTempPoint[_user] = 0;
    }
    userTempPoint[_user] += _point;
  }

  function getUserCurrentEnergy(address _user) external view returns (uint) {
    Energy storage energy = energies[msg.sender];
    bool firstConsumeInDay = block.timestamp > energy.to;
    if (firstConsumeInDay) {
      return _calculateUserTodayEnergy();
    }
    return energies[_user].amount;
  }

  // PRIVATE FUNCTIONS

  function _getStartOfDay() private view returns (uint) {
    return block.timestamp - block.timestamp % secondInADay;
  }

  function _calculateUserTodayEnergy() private view returns (uint) {
    uint owned = heroNft.balanceOf(msg.sender);
    uint rented = marketPlace.currentRentedHeroCounter(msg.sender);
    uint point = userTempPoint[msg.sender] >= 0 ? uint(userTempPoint[msg.sender]) : uint (userTempPoint[msg.sender] * -1);
    uint energy = (owned + rented - point) * energyMultiplication;
    if (energy > maxEnergy) {
      energy = maxEnergy;
    }
    return energy;
  }

  // ADMIN FUNCTIONS

  function updateMaxEnergy(uint _amount) external onlyMainAdmin {
    maxEnergy = _amount;
    emit MaxEnergyUpdated(maxEnergy, block.timestamp);
  }

  function updateEnergyMultiplication(uint _multiplication) external onlyMainAdmin {
    require(_multiplication >= 1, "EnergyManager: invalid data");
    energyMultiplication = _multiplication;
    emit EnergyMultiplicationUpdated(_multiplication, block.timestamp);
  }

  // TODO for testing purpose

  function setContracts(address _heroNft, address _marketplace) external onlyMainAdmin {
    heroNft = IGameNFT(_heroNft);
    marketPlace = IMarketPlace(_marketplace);
  }
}
