// SPDX-License-Identifier: GPL

pragma solidity 0.8.0;

import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";
import "../libs/fota/Auth.sol";
import "../interfaces/IEnergyManager.sol";
import "../interfaces/IRewardManager.sol";

contract FOTAGamePVE is Auth, EIP712Upgradeable {

  struct Mission {
    uint id;
    uint reward;
    address owner;
  }
  enum Mode {
    FREE,
    NOT_FREE
  }
  IEnergyManager public energyManager;
  IRewardManager public rewardManager;

  uint public totalMissions;
  uint public energyConsumption;
  uint public maxReward;
  Mode public mode;
  mapping (address => uint) public nonces;
  mapping (address => uint) public rewarded;
  mapping (uint => Mission) public missions;

  event MissionAdded(uint id, uint reward, address owner, uint timestamp);
  event MissionUpdated(uint id, uint reward, address owner, uint timestamp);
  event EnergyConsumptionUpdated(uint energyConsumption);
  event ModeUpdated(Mode mode, uint timestamp);
  event MaxRewardUpdated(uint amount, uint timestamp);

  function initialize(
    string memory _name,
    string memory _version,
    address _mainAdmin,
    address _energyManager
  ) public initializer {
    Auth.initialize(_mainAdmin);
    EIP712Upgradeable.__EIP712_init(_name, _version);
    energyManager = IEnergyManager(_energyManager);
    _initData();
  }

  function validateInviter(address _inviter) external view returns (bool) {
    _inviter;
    return mainAdmin != address(0);
  }

  function finishGame(uint _mission, bytes memory _signature) external {
    _validateFinishGame(_mission, _signature);
    uint energy = energyManager.getUserCurrentEnergy(msg.sender);
    bool firstConsumeInDay = energyManager.updateEnergy(msg.sender, energyConsumption);
    if (firstConsumeInDay || energy >= energyConsumption) {
      _giveReward(_mission, firstConsumeInDay);
      _experienceUp();
    } else {
      require(mode != Mode.FREE, "401");
      _giveReward(_mission, firstConsumeInDay);
    }
  }

  // PRIVATE FUNCTIONS

  function _giveReward(uint _mission, bool _firstConsumeInDay) private {
    uint reward = _calculateReward(_mission, _firstConsumeInDay);
    if (missions[_mission].owner != address(0)) {
      uint shared = reward / 100;
      reward -= shared;
      rewardManager.addLandShared(missions[_mission].owner, shared);
    }

    rewardManager.addReward(1, msg.sender, reward);
  }

  function _calculateReward(uint _mission, bool _firstConsumeInDay) private returns (uint) {
    uint reward = missions[_mission].reward;
    if (_firstConsumeInDay) {
      if (reward > maxReward) {
        reward = maxReward;
      }
      rewarded[msg.sender] = reward;
    } else {
      if (rewarded[msg.sender] + reward >= maxReward) {
        reward = maxReward - rewarded[msg.sender];
      }
      rewarded[msg.sender] += reward;
    }
    return reward;
  }

  function _experienceUp() private {
    // TODO
  }

  function _validateFinishGame(uint _mission, bytes memory _signature) private {
    require(_mission <= totalMissions, "Mission invalid");
    bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(
        keccak256("FinishGamePVE(address user,uint256 nonce,uint256 mission)"),
        msg.sender,
        nonces[msg.sender],
        _mission
      )));
    nonces[msg.sender]++;
    address signer = ECDSAUpgradeable.recover(digest, _signature);
    require(signer == contractAdmin, "MessageVerifier: invalid signature");
    require(signer != address(0), "ECDSAUpgradeable: invalid signature");
  }

  function _initData() private {
    mode = Mode.FREE;
    totalMissions = 30;
    energyConsumption = 1;
    rewardManager = IRewardManager(address(0)); // TODO
    for(uint i = 1; i <= totalMissions; i++) {
      missions[i] = Mission(i, 1e18, address(0));
    }
  }

  // ADMIN FUNCTIONS

  function addMission(uint _reward, address _owner) external onlyMainAdmin {
    totalMissions++;
    missions[totalMissions] = Mission(totalMissions, _reward, _owner);
    emit MissionAdded(totalMissions, _reward, _owner, block.timestamp);
  }

  function updateMission(uint _id, uint _reward, address _owner) external onlyMainAdmin {
    missions[_id].reward = _reward;
    missions[_id].owner = _owner;
    emit MissionUpdated(_id, _reward, _owner, block.timestamp);
  }

  function updateEnergyConsumption(uint _energyConsumption) external onlyMainAdmin {
    energyConsumption = _energyConsumption;
    emit EnergyConsumptionUpdated(energyConsumption);
  }

  function updateMode(Mode _mode) external onlyMainAdmin {
    mode = _mode;
    emit ModeUpdated(mode, block.timestamp);
  }

  function updateMaxReward(uint _maxReward) external onlyMainAdmin {
    require(_maxReward > 0, "401");
    maxReward = _maxReward;
    emit MaxRewardUpdated(maxReward, block.timestamp);
  }
}
