// SPDX-License-Identifier: MIT

pragma solidity 0.8.0;

import "../libs/fota/Auth.sol";
import "../libs/fota/MerkelProof.sol";
import "../interfaces/IGameNFT.sol";

contract HeroAirdropDistributor is Auth {

  IGameNFT public heroNFT;
  uint16 public heroClassId;
  uint public heroPrice;

  bytes32 public rootHash;
  mapping(uint => uint) private claimedBitMap;

  event Claimed(uint _index, address _address, uint _tokenId);

  function initialize(
    address _mainAdmin,
    address _heroNFT
  ) public initializer {
    Auth.initialize(_mainAdmin);
    heroClassId = 17;
    heroPrice = 175e18;
    rootHash = 0x8627ae73ef8bb3d092704dcc557d590277bf54ecf53a3d801346b176e9fb683c;
    heroNFT = IGameNFT(_heroNFT);
  }

  // OWNER FUNCTIONS

  function setRootHash(bytes32 _rootHash) onlyMainAdmin external {
    rootHash = _rootHash;
  }

  function setHeroPrice(uint _heroPrice) onlyMainAdmin external {
    heroPrice = _heroPrice;
  }

  function setHeroClassId(uint16 _heroClassId) onlyMainAdmin external {
    heroClassId = _heroClassId;
  }

  // PUBLIC FUNCTIONS

  function isClaimed(uint index) public view returns (bool) {
    uint claimedWordIndex = index / 256;
    uint claimedBitIndex = index % 256;
    uint claimedWord = claimedBitMap[claimedWordIndex];
    uint mask = (1 << claimedBitIndex);
    return claimedWord & mask == mask;
  }

  function claim(uint _index, bytes32[] calldata _path) external {
    require(!isClaimed(_index), 'AirdropDistributor: Drop already claimed.');
    bytes32 hash = keccak256(abi.encodePacked(msg.sender, _index));
    require(MerkleProof.verify(_path, rootHash, hash), 'AirdropDistributor: 400');
    _setClaimed(_index);
    uint tokenId = heroNFT.mintHero(msg.sender, heroClassId, heroPrice, 0);
    emit Claimed(_index, msg.sender, tokenId);
  }

  // PRIVATE FUNCTIONS

  function _setClaimed(uint index) private {
    uint claimedWordIndex = index / 256;
    uint claimedBitIndex = index % 256;
    claimedBitMap[claimedWordIndex] = claimedBitMap[claimedWordIndex] | (1 << claimedBitIndex);
  }
}
