// SPDX-License-Identifier: GPL

pragma solidity 0.8.0;

import "../interfaces/IGameNFT.sol";
import "./BaseUpgrading.sol";

contract SkillUpgrading is BaseUpgrading {
  IGameNFT public heroNft;
  IGameNFT public itemNft;

  struct Requirement {
    uint fee;
    uint8 successRatio;
  }
  mapping (uint8 => Requirement) public requirements;

  event RequirementUpdated(uint8 indexed section, uint fee, uint8 successRatio);
  event Upgraded(uint indexed tokenId, uint8 acceptedRatio, bool success);

  function initialize(
    string memory _name,
    string memory _version,
    address _mainAdmin,
    address _fundAdmin,
    address _heroNft,
    address _itemNft,
    address _fotaPricer
  ) public initializer {
    super.initialize(_name, _version, _mainAdmin, _fundAdmin, _fotaPricer);
    heroNft = IGameNFT(_heroNft);
    itemNft = IGameNFT(_itemNft);
    _initData();
  }

  function skillUp(
    uint _tokenId,
    uint _eatherId,
    IMarketPlace.PaymentCurrency _paymentCurrency,
    uint8 _acceptedRatio,
    string calldata _seed,
    bytes memory _signature
  ) external {
    _validateSeed(_seed, _signature);
    (uint level, uint8 skillIndex) = _validateHero(_tokenId);
    uint8 section = level == 0 ? 0 : uint8(level / 5);
    require(_acceptedRatio <= requirements[section].successRatio, "Skill Upgrading: invalid ratio");
    bool success = _upgradeSkill(_tokenId, _acceptedRatio, _seed, skillIndex);
    uint _upgradingFee = requirements[section].fee * uint(_acceptedRatio) / uint(requirements[section].successRatio);
    if (success) {
      _takeFee(_paymentCurrency, _upgradingFee);
    } else {
      (,uint failedUpgradingAmount) = heroNft.getHeroPrices(_tokenId);
      (,,, uint eatherPrice,) = itemNft.getItem(_eatherId);
      heroNft.updateFailedUpgradingAmount(_tokenId, failedUpgradingAmount + _upgradingFee + eatherPrice);
    }
    itemNft.burn(_eatherId);
  }

  // ADMIN FUNCTIONS

  function updateRequirements(uint8 _section, uint _fee, uint8 _successRatio) external onlyMainAdmin {
    require(_successRatio > 0 && _successRatio <= 100, "Ratio invalid");
    requirements[_section] = Requirement(_fee, _successRatio);
    emit RequirementUpdated(_section, _fee, _successRatio);
  }

  // PRIVATE FUNCTIONS

  function _upgradeSkill(
    uint _tokenId,
    uint8 _acceptedRatio,
    string calldata _seed,
    uint8 _skillIndex
  ) private returns (bool success) {
    success = isSuccess(_seed, _acceptedRatio);
    if (success) {
      heroNft.skillUp(_tokenId, _skillIndex);
    }
    emit Upgraded(_tokenId, _acceptedRatio, success);
  }

  function _initData() private {
    requirements[0] = Requirement(100e18, 50);
    requirements[1] = Requirement(500e18, 30);
    requirements[2] = Requirement(1000e18, 10);
    requirements[3] = Requirement(5000e18, 10);
  }

  function _validateSeed(
    string calldata _seed,
    bytes memory _signature
  ) override internal {
    bytes32 digest = _hashTypedDataV4(keccak256(abi.encode(
        keccak256("SkillUp(address user,string seed,uint256 nonce)"),
        msg.sender,
        keccak256(bytes(_seed)),
        nonces[msg.sender]
      )));
    nonces[msg.sender]++;
    address signer = ECDSAUpgradeable.recover(digest, _signature);
    require(signer == contractAdmin, "MessageVerifier: invalid signature");
    require(signer != address(0), "ECDSAUpgradeable: invalid signature");
  }

  function _validateHero(uint _tokenId) private view returns (uint, uint8) {
    (,,,,,uint8 level, uint experience) = heroNft.getHero(_tokenId);
    uint32 experienceCheckpoint = heroNft.experienceCheckpoint(level + 1);
    require(experience == experienceCheckpoint, "Skill Upgrading: Experience invalid");
    uint8 levelMod5 = uint8(level % 5);
    require(levelMod5 != 1 && levelMod5 != 3, "Skill Upgrading: Level invalid");
    uint8 skillIndex = levelMod5 == 0 ? 0 : levelMod5 == 2 ? 1 : 2;
    return (level, skillIndex);
  }

  function _takeFee(IMarketPlace.PaymentCurrency _paymentCurrency, uint _upgradingFee) private {
    takeFund(_paymentCurrency, _upgradingFee, Implementation.Hero);
  }

  // TODO for testing purpose
  function setToken(address _fotaToken, address _busdToken, address _usdtToken) external onlyMainAdmin {
    fotaToken = IBEP20(_fotaToken);
    busdToken = IBEP20(_busdToken);
    usdtToken = IBEP20(_usdtToken);
  }
}
